dfree() { local IFS=$'\n\t '; df=($(df -H|grep -E "storage|Size"|tr -s " " " "|cut -f2-6 -d" ")); dflines="$((${#df[*]} / 5))"; aa=0; kk=0; for i in ${df[*]}; do for a in {4,0,1,2,3}; do p=$(printf %b "${df[((a+aa))]} "); [ $p ] && printf %b "$p"|bat -ppfljava --theme DarkNeon; ((kk++)); done; [ $((kk / 5 - 1)) -lt $dflines ] && [ $aa -gt 0 ] && (sa="${df[((a+aa))]}"; as=${sa:0:1}; case $as in 0) oc=28;; 1) oc=196;; 2) oc=196;; 3) oc=166;; 4) oc=130;; 5) oc=130;; 6) oc=94;; 7) oc=100;; 8) oc=64;; 9) oc=64;; esac; mm=44; ii=6; printf %b " \e[90m\u25ac\e[10b\e[12D \e[38;5;${oc}m\u25ac\e[${as}b\e[0m"; ); aa=$kk; printf %b "\n"; done|column --table --table-columns-limit 5 --output-separator " | "; }; 
