sshh() { qmenu "ssh/mosh - ip address" "$(printf %b 192.168.0.{1..22}\\n)"; selip="${sel}"; qmenu "port" "$(printf %b 22\\n8022)"; selpo="${sel}"; printf %b "\n\n\e[A -- user "; read -rep ' ' -i "aa" "selus"; printf %b "mosh -P $selpo ${selus}@${selip}\n"|tee -a $HISTFILE; echo; history -a; mosh -P $selpo ${selus}@${selip}; printf %b "\n-------- mosh exit --------\n\n"; (uname -a; echo; uptime)|bat -ppfljava --theme DarkNeon; echo; }; 
