#!/bin/bash
####
word() { local IFS=$' \n\t'; fold="$HOME/logs/words/wordlog"; sh $HOME/88/i/words/wpre.sh 2>/dev/null; printf %b "\n\n\e[A"; [ "$1" ] && wordget="$@" || read -rep " -- word: " "wordget" && printf %b "\n\e[2A\e[K"; printf %b "\n----\n"; lynx -force_empty_hrefless_a -nomargins -width 800 -nonumbers -dump "https://www.dictionary.com/browse/${wordget}"|head -n188|grep -vE "\(BUTTON\)|_|Phonetic|Advertisement|View\ synonyms\ for|Skip\ to"|grep -e "\ 1.\ " -m1 -B12|sed -e "/\^[1-9]/d" -e "/([1-9])/d" -e "s/\ 1\.\ /\n__\n/"|sed -e "/plural/,/__/d" -e "/^$/d" -e "/_/d"|tail -n4|tee $fold/$wordget.log; printf %b "----\n\n"; }; 
