dd="$HOME/logs/df"; mkdir -m 775 -p $dd 2>/dev/null; (unset -v aa a i df dfl; IFS=$'\n\t'; df=($(df -h | grep -v "tmpfs")); dfl="${!df[*]}"; declare -i a; a=0; IFS=$'\n\t '; for i in ${df[*]}; do ((a++)); printf %b "${df[a]}"|tr -s "\n\t " " "| sed -e "i\)\;\ \naa$a\=\("|tr -d "\n"; done|tail -c+4|sed -e "s/; /;\n/g"; printf %b ");") | tee $HOME/logs/df.sh; chmod 775 $HOME/logs/df.sh; IFS=$'\n\t '; unset -v aa df dfl a i; declare -A aa; . $HOME/logs/df.sh; printf %b "${aa[*]}";
